import React, { Component} from 'react';

class Hello extends Component
{
    render()
    {
        const { name,age,about } =this.props
        //console.log(this.props);
        return(
            <div className="hello">
                <h1>
                    My FIRST REACT App!!
                    
                </h1>
                <h2>
                    {/* Name: {this.props.name} age: {this.props.age} About: {this.props.about} */}
                    <p>Name: {name}</p>
                    <p>Age: {age}</p>
                    <p>About: {about}</p>
                </h2>
            </div>
        );
    }
}

export default Hello;
