import React, { Component} from 'react';

const Hello = (props) =>
{
        const { hello } =props;
        const hello_list=hello.map(hell => {
            if(hell.age>20)
            {
            //console.log(this.props);
            return(
            <div className="hello">
                
                <h2>
                    <p>Name: {hell.name}</p>
                    <p>Age: {hell.age}</p>
                    <p>About: {hell.about}</p>
                </h2>
            </div>
            );
            }
            else
            {
                return null
            }
        })
        return(
            <div className="hello-list">
            {hello_list}
            </div>
            );
}

export default Hello;
